def count_nucleotides(dna_string):
    # Contamos las ocurrencias de cada nucle√≥tido en la cadena
    count_A = dna_string.count('A')
    count_C = dna_string.count('C')
    count_G = dna_string.count('G')
    count_T = dna_string.count('T')
    
    # Imprimir los conteos en el orden requerido
    print(count_A, count_C, count_G, count_T)

# Uso para resolver caso
dna_sample = ""
count_nucleotides(dna_sample)
