def distancia_hamming(s, t):
    # Verificar que ambas cadenas tengan la misma longitud de lo contrario dara error
    if len(s) != len(t):
        raise ValueError("Las secuencias deben tener la misma longitud") 

    # Contar las diferencias entre las cadenas
    distancia = sum(1 for i in range(len(s)) if s[i] != t[i])

    return distancia

# Ejecusion para resolver caso
s = ""
t = ""
resultado = distancia_hamming(s, t)
print(f"Distancia de Hamming: {resultado}")
